function r(t) {
  /in/.test(document.readyState) ? setTimeout("r(" + t + ")", 9) : t();
}
function updateRates() {
  var t = new XMLHttpRequest();
  t.open(
    "GET",
    "https://www.overbit.com/app/data?symbols=ADAUSD,ADABTC,AUDUSD,BCHUSD,BCHBTC,EOSUSD,ETHUSD,ETHBTC,EURJPY,EURUSD,GBPUSD,LTCUSD,LTCBTC,NEOUSD,NEOBTC,USDJPY,XAGUSD,XAUUSD,BTCJPY,BTCUSD,XRPUSD,XRPBTC",
    !0
  ),
    (t.onreadystatechange = function() {
      if (4 == t.readyState && 200 == t.status) {
        var e = JSON.parse(t.responseText);
        Object.keys(e).forEach(function(t) {
          Object.keys(e[t]).forEach(function(n) {
            var a = document.getElementById("rate_" + t + "_" + n);
            null != a && (a.innerHTML = e[t][n]);
          });
        }),
          setTimeout(updateRates, 1e3);
      }
    }),
    t.send(null);
}
r(function() {
  updateRates();
});
